
export class McpServer {
  constructor(config) {
    this.name = config.name;
    this.version = config.version;
    this.tools = [];
  }

  tool(name, schema, func) {
    this.tools.push({ name, schema, func });
  }

  async connect(transport) {
    console.log(`${this.name} (v${this.version}) is now listening via MCP transport.`);
    await transport.start();
  }
}
